pragma solidity ^0.5.0;

import "./common/Ownable.sol";
import "./Vestrade_Offering.sol";

contract OfferingFactory is Ownable {
	event OfferingEvent(address addr, address tokenAddr, uint256 supply, uint256 price, uint64 startDate, uint64 endDate);

	function create(
		address _tokenAddr, 
		uint256 _supply, 
		uint256 _price, 
		uint64 _startDate, 
		uint64 _endDate
	) onlyOwner public {
		Vestrade_Offering newOffering = new Vestrade_Offering(_tokenAddr, _supply, _price, _startDate, _endDate);

		newOffering.transferOwnership(msg.sender);

		emit OfferingEvent(address(newOffering), address(_tokenAddr), _supply, _price, _startDate, _endDate);
	}
}
